import React, { useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import './home.scss'; // Import the SCSS file for styling
import securityImage from '/media/security.jpg'; // Import the security image

const Home = () => {
  const [url, setUrl] = useState('');
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('/detect-phishing', { url });
      setResult(response.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <Container>
      <Row className="justify-content-center mt-5">
        <Col md={8}>
          <div className="form-container">
            <img src={securityImage} alt="Security" className="security-image" />
            <h2 className="text-center mb-4">Phishing Website Detector</h2>
            <Form onSubmit={handleSubmit}>
              <Form.Group controlId="urlInput">
                <Form.Label>Enter URL:</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter URL"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                  required
                />
              </Form.Group>
              <Button variant="primary" type="submit" className="btn-detect">
                Detect Phishing
              </Button>
            </Form>
            {result && (
              <div className="result-container mt-4">
                <h3 className="result-heading">Detection Result:</h3>
                <p className="result-text">{result}</p>
              </div>
            )}
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Home;
