import React, { useState } from 'react';
import axios from 'axios';
import './home.scss'; // Import SCSS file
// import { Link } from 'react-router-dom';

function Home() {
    const [url, setUrl] = useState('');
    const [result, setResult] = useState(null);

    const handleCheckUrl = async () => {
        try {
            // Make an API call to your backend to check if the URL is malicious
            const response = await axios.post('/api/check-url', { url });
            setResult(response.data);
        } catch (error) {
            console.error('Error checking URL:', error);
        }
    };

    return (
        <div className="home-container">
            <h2>Phishing Website Detector</h2>
            <p>Welcome to our phishing website detector. Enter the URL below to check if it's malicious.</p>
            <input type="text" placeholder="Enter URL" value={url} onChange={(e) => setUrl(e.target.value)} />
            <button onClick={handleCheckUrl}>Check URL</button>
            {result && (
                <div className="result-container">
                    <h3>Result</h3>
                    {result.malicious ? (
                        <p>The website is malicious.</p>
                    ) : (
                        <p>The website is safe.</p>
                    )}
                </div>
            )}
        </div>
    );
}

export default Home;
